import React, { useState, useReducer } from "react";

import Card from "../UI/Card/Card";
import classes from "./Login.module.css";
import Button from "../UI/Button/Button";

const Login = (props) => {
  // const [enteredEmail, setEnteredEmail] = useState("");
  // const [emailIsValid, setEmailIsValid] = useState();
  const [enteredPassword, setEnteredPassword] = useState("");
  const [passwordIsValid, setPasswordIsValid] = useState();
  const [formIsValid, setFormIsValid] = useState(false);

  // useEffect(() => {
  //  const TimerId=  setTimeout(() => {
  //     console.log(enteredEmail , 'up');
  //     setFormIsValid(
  //       enteredEmail.includes("@") && enteredPassword.trim().length > 6
  //     );
  //   },500);
  //   return () => {
  //     console.log(enteredEmail, 'down');
  //     clearTimeout(TimerId); //
  //   };
  // }, [enteredEmail, enteredPassword]);

  const emailReducer = (state, actions) => {
     if(actions.type === 'email') { 
      return {value : actions.value , isValid : actions.value.includes('@')};
     }if(actions.type === 'INPUT'){ 
      return {value : state.value  , isValid:state.value.includes('@')} 
     }  
     return {value : 'this is not email'}  
  };

  const [emailState, dispatchEmail] = useReducer(emailReducer, {type:'email',value:'',isValid:true});

  const emailChangeHandler = (event) => {
    // setEnteredEmail(event.target.value);
     dispatchEmail({
      type:'email',
      value:event.target.value,
     })

  };

  const passwordChangeHandler = (event) => {
    setEnteredPassword(event.target.value);
     // in this case we need a reducer 
     setFormIsValid(emailState.isValid && event.target.value.trim().length > 6);
  };

  const validateEmailHandler = () => {
    // setEmailIsValid(enteredEmail.includes("@"));    
    dispatchEmail({type:'INPUT'})
  };

  const validatePasswordHandler = (event) => {
   setPasswordIsValid(event.target.value.trim().length > 6)
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(emailState.value, enteredPassword);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className = {`${classes.control} ${
            emailState.isValid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={emailState.value}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordIsValid === false ? classes.invalid : ""
          }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={enteredPassword}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
